const skills: Skill[] = [
  { name: "Laravel", color: "from-red-500 to-orange-500", icon: <LaravelIcon /> },
  { name: "Python", color: "from-yellow-500 to-blue-500", icon: <PythonIcon /> },
  { name: "HTML/CSS", color: "from-orange-500 to-pink-500", icon: <HtmlCssIcon /> },
  { name: "SQL Databases", color: "from-blue-500 to-cyan-500", icon: <DatabaseIcon /> },
  { name: "React", color: "from-cyan-500 to-blue-500", icon: <ReactIcon /> },
  { name: "JavaScript", color: "from-yellow-400 to-yellow-500", icon: <JavaScriptIcon /> },
  { name: "Figma", color: "from-pink-500 to-purple-500", icon: <FigmaIcon /> },
  { name: "Canva", color: "from-cyan-400 to-purple-500", icon: <CanvaIcon /> },
  { name: "Arduino", color: "from-teal-500 to-cyan-600", icon: <ArduinoIcon /> },
];

const SkillCard = ({ skill, index, isInView }: { skill: Skill; index: number; isInView: boolean }) => {
  return (
    <motion.div
      className="glass-card-hover p-8 flex flex-col items-center gap-4"
      initial={{ opacity: 0, y: 50 }}
      animate={isInView ? { opacity: 1, y: 0 } : {}}
      transition={{ duration: 0.6, delay: index * 0.1 }}
      whileHover={{ scale: 1.05 }}
    >
      <div className="w-20 h-20 flex items-center justify-center">
        {skill.icon}
      </div>
      
      <h3 className="font-heading font-semibold text-lg text-foreground text-center">{skill.name}</h3>
    </motion.div>
  );
};

const Skills = () => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, margin: "-100px" });

  return (
    <section id="skills" className="relative py-32 overflow-hidden" ref={ref}>
      {/* Background */}
      <div className="absolute inset-0 cyber-grid opacity-10" />
      
      {/* Gradient orb */}
      <motion.div
        className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-[600px] h-[600px] rounded-full blur-[200px] pointer-events-none"
        style={{
          background: "radial-gradient(circle, hsl(var(--cyber-cyan) / 0.1) 0%, transparent 70%)",
        }}
      />

      <div className="container relative z-10 px-6">
        {/* Section header */}
        <motion.div
          className="text-center space-y-4 mb-16"
          initial={{ opacity: 0, y: 30 }}
          animate={isInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.6 }}
        >
          <motion.div
            className="inline-flex items-center gap-2 px-4 py-2 rounded-full glass-card"
            initial={{ opacity: 0, y: 20 }}
            animate={isInView ? { opacity: 1, y: 0 } : {}}
            transition={{ delay: 0.2 }}
          >
            <span className="w-2 h-2 rounded-full bg-cyber-emerald" />
            <span className="text-sm text-muted-foreground font-body uppercase tracking-widest">
              Technical Skills
            </span>
          </motion.div>
          
          <h2 className="text-4xl md:text-5xl font-heading font-bold">
            My <span className="gradient-text">Expertise</span>
          </h2>
          <p className="text-muted-foreground font-body max-w-2xl mx-auto">
            Proficient in a wide range of technologies, from backend frameworks to frontend libraries
          </p>
        </motion.div>

        {/* Skills display - Grid layout */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-6 mb-16 max-w-5xl mx-auto">
          {skills.map((skill, index) => (
            <SkillCard
              key={skill.name}
              skill={skill}
              index={index}
              isInView={isInView}
            />
          ))}
        </div>

        {/* Additional technologies */}
        <motion.div
          className="mt-16 text-center"
          initial={{ opacity: 0, y: 30 }}
          animate={isInView ? { opacity: 1, y: 0 } : {}}
          transition={{ delay: 0.8 }}
        >
          <p className="text-sm text-muted-foreground font-body mb-4">
            Also experienced with:
          </p>
          <div className="flex flex-wrap justify-center gap-3">
            {["REST APIs", "Tailwind CSS", "Internet of Things", "bootstrap 5"].map((tech, index) => (
              <motion.span
                key={tech}
                className="px-4 py-2 rounded-full glass-card text-sm font-body text-foreground hover:border-primary/50 transition-colors cursor-default"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={isInView ? { opacity: 1, scale: 1 } : {}}
                transition={{ delay: 1 + index * 0.05 }}
                whileHover={{ scale: 1.05 }}
              >
                {tech}
              </motion.span>
            ))}
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Skills;